import java.io.*;class TicTacToe1 {    int player = -1;    char array[][] = {{' ', ' ', ' '}, {' ', ' ', ' '}, {' ', ' ', ' '}};    char win;    public void displayPositions() {        for (int i = 0; i < 3; i++) {            for (int j = 0; j < 3; j++)                System.out.print(i * 3 + j + 1 + " ");            System.out.print("\n");        }    }    public void drawTicTacToe1() {        for (int i = 0; i < 11; i++) {            if (i == 0 || i == 2 || i == 4 || i == 6 || i == 8 || i == 10) {                for (int j = 0; j < 3; j++) {                    System.out.print("       ");                    if (j < 2)                        System.out.print("|");                }            } else if (i == 1 || i == 5 || i == 9) {                for (int j = 0; j < 3; j++) {                    System.out.print("   " + array[i / 4][j] + "   ");                    if (j < 2)                        System.out.print("|");                }            } else {                for (int j = 0; j < 3; j++) {                    System.out.print("-------");                    if (j < 2)                        System.out.print("|");                }            }            System.out.println();        }    }    public int getInput() throws IOException {        int input;        BufferedReader in = new BufferedReader(new InputStreamReader(System.in));        System.out.println("Enter the position where you want to place");        input = Integer.parseInt(in.readLine());        return input;    }    public boolean checkValid(int row, int col) {        if (row < 0 || row > 2 || col < 0 || col > 2 || array[row][col] == 'X' || array[row][col] == 'O')            return false;        else            return true;    }    public void display() {        System.out.println("\n");        for (int i = 0; i < 3; i++) {            for (int j = 0; j < 3; j++)                System.out.print(array[i][j] + "\t");            System.out.println("\n");        }    }    public void horizontal_check() {        char ch;        int count = 0;        for (int x = 0; x < 3; x++) {            if (array[x][0] != ' ') {                ch = array[x][0];                count = 1;                for (int y = 1; y < 3; y++) {                    if (array[x][y] == ch)                        count++;                    if (count == 3)                        win = ch;                }            }        }    }    public void vert_check() {        char ch;        int count = 0;        for (int x = 0; x < 3; x++) {            if (array[0][x] != ' ') {                ch = array[0][x];                count = 1;                for (int y = 1; y < 3; y++) {                    if (array[y][x] == ch)                        count++;                    if (count == 3) {                        win = ch;                    }                }            }        }    }    public void diag_check() {        if (array[1][1] != ' ') {            if (array[1][1] == array[0][0] && array[1][1] == array[2][2])                win = array[1][1];            else if (array[1][1] == array[0][2] && array[1][1] == array[2][0])                win = array[1][1];        }    }    public void run() throws IOException {        int row, col, input;        boolean valid = false;        for (int times = 1; times <= 9; times++) {            player = player * -1;            drawTicTacToe1();            if (player > 0)                System.out.println("\nFirst player (X) chance - ");            else                System.out.println("\nSecond player (O) chance - ");            do {                displayPositions();                input = getInput();                row = (input - 1) / 3;                col = (input - 1) % 3;                valid = checkValid(row, col);                if (!valid) {                    System.out.println(" \n Wrong Input  , Try Again");                }            } while (!valid);            if (player > 0)                array[row][col] = 'X';            else                array[row][col] = 'O';            horizontal_check();            vert_check();            diag_check();            if (win == 'X') {                System.out.println("First Player (X) wins  !!!");                return;            } else if (win == 'O') {                System.out.println("Second Player (O) wins  !!!");                return;            }        }        System.out.println("Draw Match");    }    public static void main(String args[]) throws IOException {        TicTacToe tictactoe1 = new TicTacToe();        tictactoe1.run();        tictactoe1.drawTicTacToe();    }}